import React, { Component } from 'react';
import axios from 'axios';
import endPointObj from '../endPointUrl.js';
import ProductCard from "./Product/ProductCard.js";
import { InputGroup, FormControl, Button, Form, Dropdown, Alert, Col, Row, FormLabel } from 'react-bootstrap';
import {Redirect} from 'react-router';

class Home extends Component {
    constructor(props) {
        super(props);
        this.setState({
            search_input: "",
        });

        this.onChange = this.onChange.bind(this);
        this.onSearch = this.onSearch.bind(this);
    }

    componentDidMount() {
        console.log("Mounting the component");
        axios.get(endPointObj.url + "/marketplace/getProducts")
            .then(response => {
                if (response.data) {
                    console.log("marketplace item response", response.data);
                    this.setState({
                        postsList: response.data,
                        displayPosts: response.data
                    });
                }
            })
            .catch(error => {
                if (error.response && error.response.data) {
                    console.log(error.response.data);
                }
            })
    }

    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value,
            noRecord: false
        });
    }

    onSearch = (e) => {
        e.preventDefault();
        
        if (this.state) {
            var searchInput = typeof this.state.search_input === "undefined" || this.state.search_input === "" ? "_" : this.state.search_input;
            let data = {
                searchInput : searchInput
            }
           
            axios.post(endPointObj.url + "/restaurant/restaurantDeliverySearch", data)
                .then(response => {
                    if (response.data) {
                        if (response.data === 'NO_RECORD') {
                            this.setState({
                                noRecord: true,
                                search_input: searchInput
                            });
                        }
                        else {
                            this.setState({
                                postsList: response.data,
                                displayPosts: response.data,
                                noRecord: false
                            });
                        }
                    }
                })
                .catch(error => {
                    if (error.response && error.response.data) {
                        console.log(error.response.data);
                    }
                })
        }
    }

    render() {
        var postCards = null,
            noRecordMessage = null;
        let redirectVar = null;  

        if (this.state && this.state.displayPosts) {
            postCards = this.state.displayPosts.map(posts => {
                return (
                    <Col md={4}>
                        <ProductCard productPost={posts} />
                    </Col>
                );
            });
        }

        if (this.state && this.state.noRecord && this.state.search_input === "") {
            noRecordMessage = (
                <Alert variant="warning">
                    Sorry,Item you have searched is not available!
                </Alert>
            );
        }
        else if(this.state && this.state.noRecord){
            noRecordMessage = (
            <Alert variant="warning">
                    No Results. Please try again.
                </Alert>
            );
        }
        else {
            noRecordMessage = null;
        }

        return (
            <div>
                {redirectVar}
                <center><br /><br />
                    
                    <br />
                    <div><br/><br/> 
                    <h4>Search for your favourite items</h4><br/><br/>
                    <Form onSubmit={this.onSearch}>
                        <InputGroup style={{ width: '90%', height: '100%', paddingTop: '0em'}} size="lg">
                            <FormControl
                                style={{ width: '90%', height: '100%'}}
                                placeholder="Search for your favourite products"
                                aria-label="Search products"
                                aria-describedby="basic-addon2"
                                name="search_input"
                                className = "searchInputField"
                                onChange={this.onChange}
                            /> <br /><br /><br/><br/>
                            <br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                            <center><Button variant="primary" type="submit" className="restoSearchButton"><h5><b>Search</b></h5></Button></center>
                          
                            
                        </InputGroup> 
                    </Form>
                    </div>

                    <div className="welcomeTitle">
                    </div>
                    <br /><br />
                    {noRecordMessage}
                    <Row md="6">{postCards}</Row>
                
                </center>
            </div>
        )
    }
}

export default Home;
