import React, { Component } from "react";
import { Card } from "react-bootstrap";
import { Link } from "react-router-dom";
import endPointObj from '../../endPointUrl.js';


class PostCard extends Component {
 
  render() {

    var productData = this.props.product;
    let imageSrc = endPointObj.url + "/images/product/" + this.props.product.item_image;
    console.log("Image src", imageSrc);
    return (
      <Card bg="white" style={{ width: "20rem", margin: "5%" }}>
      <Link to={{pathname: '/restaurant', state: productData}}> 
        <Card.Body>
          <Card.Img
            style={{ width: "18rem", height: "15em" }}
            src={imageSrc}
          />
          <Card.Title style={{ fontSize: "2rem", margin: "5%", color: "#76b900" }}><b>{this.props.product.itemName}</b></Card.Title>
         
          <Card.Text>{this.props.product.itemDescription}</Card.Text>
        </Card.Body>
      </Link>
      </Card>
    );
  }
}

export default PostCard;
