import React, { Component } from 'react';
import { Redirect } from 'react-router';
import axios from 'axios';
import { Button, Card, Modal, Col, Row, Alert } from 'react-bootstrap';
import endPointObj from '../../endPointUrl.js';
import { Link } from "react-router-dom";
import "./Product.css";
import NavigationBar from "../Navbar/Navbar.js";


var renderedOutput, selected_image;
var images = [];
class ProductDetails extends Component {
    constructor(props) {
        super(props);
        this.setState({
            productDetails: "",
            showModal: false,

        });
        this.openModal = this.openModal.bind(this);
        this.onClose = this.onClose.bind(this);
        this.showImages = this.showImages.bind(this);
    }

    showImages = (e) => {
        console.log("Inside show images");
        renderedOutput = this.state.images.map(item =>

            <div class="images_preview" style={{ marginBottom: '15px' }}>
                <img src={endPointObj.url + '/images/product/' + item.file_name} style={{ width: '80px' }} alt="im" />

            </div>
        )
    }

    openModal = () => {
        this.setState({
          showModal: true
        });
    };
    
    onClose = (e) => {
        this.setState({
          showModal: false
        });
    }

    
    render() {
        let itemImageSrc = null,
            itemName, itemDescription, price, location, contact,item_image,
            item = this.props.location.state;
        let showModal = false;
        let buttonClick = this.openModal;
        var noRecordMessage = null, imageCards = null;
            console.log("Item details:", item);

        if (item) {
            itemImageSrc = endPointObj.url + "/images/product/" + item.item_image;
            itemName = item.itemName;
            itemDescription = item.itemDescription;
            price = item.price;
            location = item.location;
            contact = item.contact;
            images = item.images;
            //selected_image: item.images[0].file_name;
        }

        if (this.state && this.state.noRecord) {
            noRecordMessage = (
                <Alert variant="warning">
                    Product description is not available at this time!
                </Alert>
            );
        }

        if(this.state){
            showModal = this.state.showModal;
        }

        imageCards = images.map(item => { 
            return (                          
                    <Col md={6}>
                        <img src={endPointObj.url + '/images/product/' + item.file_name} style={{ width: '200px', height: '200px', margin : "5%" }} alt="im" />
                        &nbsp;&nbsp;
                    </Col>
               
            ); 
        })


        return (
            <div>
                 <NavigationBar />
                <div className="productContainer">
            
                <Card style={{ width: "50rem", height: "20rem", margin: "2%", backgroundColor: "#ffffff", paddingRight:"0em" }}>
                    
                <Col md={12}>
                    <Row >
                            <Card.Img style={{ width: "50rem", height: "20rem" }} src={itemImageSrc} />
                    </Row>
                    <Row >    
                        <Card.Body>
                            <Card.Title><h2 className="productdetailsTitle"><b>{itemName}</b> &nbsp;<label style={{ fontSize:"40px", color:"#76b900" }}><u>${price}</u></label></h2></Card.Title>
                            <hr/>
                            <Card.Text className="productdetailsDescription"><b>{itemDescription}</b> </Card.Text>
                            <br/>
                            <Card.Text className="productdetailsDescription"><u>Contact Information</u>: {location} | {contact}</Card.Text>
                        </Card.Body>
                    </Row>    
                </Col>
                <center>
                <Button variant="primary" onClick={buttonClick} name="viewImages" className="submitButton">View Images</Button>
                </center>
                
                </Card>
                <div id="myModal" className="Modal is-hidden">
                    <Modal show={showModal} onHide={this.onClose}>
                        <Modal.Body>
                            <center>
                            <Row md="3">{imageCards}</Row>                           
                            </center>
                        </Modal.Body>
                        <Modal.Footer>
                        <Button variant="secondary" className="CloseButton" onClick={this.onClose}>Close</Button>
                        </Modal.Footer>
                    </Modal>
                </div>
                               
                <br/>
                </div>
            </div>
        )
    }
}
export default ProductDetails;
