const AddPostReducer = (state, action) => {
    switch (action.type) {
        case 'GET_POST':
            return {
                ...state,
                posts: state.posts.concat(action.payload)
            };
        case 'SET_ERROR':
            return {
                ...state,
                error: action.payload
            };
        default:
            return state;
    }
};

export default AddPostReducer;
