import React, { Component } from 'react';
import { Redirect } from 'react-router';
import axios from 'axios';
import { Button, Card, Container, Col, Row } from 'react-bootstrap';
import endPointObj from '../../endPointUrl.js';
import { Link } from "react-router-dom";
import "./Product.css";


class ProductDetails extends Component {
    constructor(props) {
        super(props);
        this.setState({
            productDetails: ""
        });
        this.getProductDetails();
    }

    componentWillMount() {
        if (this.props.location.state) {
            document.title = this.props.location.state.itemName;
        }
    }


    getProductDetails = () => {
        if (this.props.location.state) {
            axios.get(endPointObj.url + "/product/getProduct/" + this.props.location.state.itemId)
                .then(response => {
                    console.log("Get product details response", response);
                    if (response.data === 'ITEM_DETAILS_NOT_FOUND') {
                        this.setState({
                            noRecord: true,
                        });
                    } else {
                        this.setState({
                            productDetails: response.data,
                            noRecord: false
                        });
                    }
                })
                .catch(err => {
                    if (err.response && err.response.data) {
                        console.log(err.response.data);
                    }
                });
        }
    };

    render() {
        let redirectVar = null,
            renderOutput = [],
            itemImageSrc = null,
            itemName, itemDescription, price, location, contact,item_image,
            item = this.props.location.state;

        if (item) {
            itemImageSrc = endPointObj.url + "/images/restaurant/" + restaurant.item_image;
            itemName = item.itemName;
            itemDescription = item.itemDescription;
            price = item.price;
            location = item.location;
            contact = item.contact;
        }
        if (this.state && this.state.noRecord) {
            noRecordMessage = (
                <Alert variant="warning">
                    Product description is not available at this time!
                </Alert>
            );
        }
        return (
            <div style={{paddingLeft:"0em", paddingRight:"-30em"}}>
                {redirectVar}
                <NavigationBar />

                <Card style={{ width: "95%", height: "20rem", margin: "2%", backgroundColor: "#ffffff", paddingRight:"0em" }}>
                    <Row>
                        <Col md={7}>
                            <Card.Img style={{ width: "50rem", height: "20rem" }} src={resImageSrc} />
                        </Col>
                        <Col>
                        <Card.Body md={4}>
                            <Card.Title><h2 style={{ color:"#000000" }}>{resName}</h2></Card.Title>
                            <Col md={2}><Card.Text style={{ width: "17rem", color:"#000000" }}><b>Location:</b> {resAddress} | {resZIP}</Card.Text></Col>
                            <Col md={2}><Card.Text style={{ width: "20rem", color:"#000000"  }}><b>Contact:</b> <Link>{restoEmail}</Link> | {resPhone}</Card.Text></Col>
                            <Col md={2}><Card.Text style={{ width: "17rem", color:"#000000"  }}><b>About us:</b> {restoDescription}</Card.Text></Col>
                            <Col md={2}><Card.Text style={{ width: "17rem",color:"#000000"  }}><b>Timings:</b> <br/> {resTiming}</Card.Text></Col>
                        </Card.Body>
                        </Col>
                    </Row>
                </Card>

                <div className = "restoMenuDisplay">
                    {renderOutput}
                </div>

                <br/>
            </div>
        )
    }
}
export default ProductDetails;
