import React, { Component } from 'react';
import { Redirect } from 'react-router';
import axios from 'axios';
import { Button, Card, Container, Col, Row, Alert } from 'react-bootstrap';
import endPointObj from '../../endPointUrl.js';
import { Link } from "react-router-dom";
import "./Product.css";
import NavigationBar from "../Navbar/Navbar.js";


var renderedOutput, selected_image;
var images = [];
class ProductDetails extends Component {
    constructor(props) {
        super(props);
        this.setState({
            productDetails: "",
        });
        this.showImages = this.showImages.bind(this);
    }

    showImages = (e) => {
        console.log("Inside show images");
        renderedOutput = this.state.images.map(item =>

            <div class="images_preview" style={{ marginBottom: '15px' }}>
                <img src={endPointObj.url + '/images/product/' + item.file_name} style={{ width: '80px' }} alt="im" />

            </div>
        )
    }

    
    render() {
        let itemImageSrc = null,
            itemName, itemDescription, price, location, contact,item_image,
            item = this.props.location.state;
      
        var noRecordMessage = null;
            console.log("Itemmmm:", item);

        if (item) {
            itemImageSrc = endPointObj.url + "/images/product/" + item.item_image;
            itemName = item.itemName;
            itemDescription = item.itemDescription;
            price = item.price;
            location = item.location;
            contact = item.contact;
            images = item.images;
            //selected_image: item.images[0].file_name;
        }

        if (this.state && this.state.noRecord) {
            noRecordMessage = (
                <Alert variant="warning">
                    Product description is not available at this time!
                </Alert>
            );
        }

        if(this.state){
            showModal = this.state.showModal;
        }

        return (
            <div style={{paddingLeft:"0em", paddingRight:"-30em"}}>
                <NavigationBar />

                <Card style={{ width: "30rem", height: "20rem", margin: "2%", backgroundColor: "#ffffff", paddingRight:"0em" }}>
                    
                <Col md={12}>
                    <Row >
                            <Card.Img style={{ width: "30rem", height: "20rem" }} src={itemImageSrc} />
                    </Row>
                    <Row >    
                        <Card.Body>
                            <Card.Title><h2 className="productdetailsTitle"><b>{itemName}</b> &nbsp;<label style={{ fontSize:"40px", color:"#76b900" }}><u>${price}</u></label></h2></Card.Title>
                            <hr/>
                            <Card.Text className="productdetailsDescription"><b>{itemDescription}</b> </Card.Text>
                            <br/>
                            <Card.Text className="productdetailsDescription"><u>Contact Information</u>: {location} | {contact}</Card.Text>
                        </Card.Body>
                    </Row>    
                </Col>
                </Card>
                <div id="myModal" className="Modal is-hidden">
                    <Modal show={showModal} onHide={this.onClose} className="Modal-content">
                        <Modal.Header>
                        <Modal.Title className="itemCartTitle">{this.props.menu_item.item_name}</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                        <center>
                            <img src={imageSrc} width="50%"  alt="" />
                            <p>{this.props.menu_item.item_description}</p>
                            Quantity: <input type="number" name={this.props.menu_item.item_id} min="1" max="10" width="10%" onChange={this.onQuantityChange} defaultValue="1" autofocus></input>
                        </center>
                        </Modal.Body>
                        <Modal.Footer>
                        <Button variant="secondary" className="Close" onClick={this.onClose}>
                            Close
                        </Button>
                        <Button className="addToCartButton" onClick={this.addToCart}>
                            Add to Cart
                        </Button>
                        </Modal.Footer>
                    </Modal>
                </div>
                <div className="image-preview">
                { images.map(item =>

                    <div style={{ marginBottom: '15px' }}>
                            <div className="col-md-4">
                            <img src={endPointObj.url + '/images/product/' + item.file_name} style={{ width: '200px', height: '200px' }} alt="im" />
                            </div>
                        
                    </div>
                ) }
                </div>
                    
                

                
                <br/>
            </div>
        )
    }
}
export default ProductDetails;
