import React, { useEffect, useState } from 'react';
import axios from 'axios';
import endPointObj from '../endPointUrl.js';
import { Form, Col, Row, Button, Alert, Card } from "react-bootstrap";


function Post() {

    //item -> Current state , addItem -> Function to set current set.
    const [itemName, setItemName] = useState('');
    const [itemDescription, setItemDescription] = useState('');
    const [price, setPrice] = useState('');
    const [location, setLocation] = useState('');
    const [contact, setContact] = useState('');
    const [itemImage, setItemImage] = useState('');
    const [alert, setAlert] = useState('');


    const addItem = (e) => {
        e.preventDefault();
        axios.post(endPointObj.url + "/marketplace/addProduct", {
            itemName: itemName,
            itemDescription:itemDescription,
            price: price,
            location: location,
            contact: contact    
        })
        .then((response) => {
            console.log("Product list after adding item", response.data);
            setAlert('Item successfully added');
        })
        .catch((err) => {
            console.log('error');
            if (err.response && err.response.data) {
                setAlert(err);
            }
        });
    }

    return(
      <div className = "restoMenuEditContainer">
           
            <Row>
                <Col xs={6} md={4}>
                    <center>
                    <br/><br/>
                    <h3>Add New Product Item</h3><br />
                    <Form onSubmit={addItem}>
                        <Form.Group as={Row} controlId="item_name">
                            <Form.Label column sm="3">
                                Item Name:
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "15rem" }} 
                                type="text" name="item_name" 
                                placeholder="Enter item name" 
                                onChange={(e) => {
                                    setItemName(e.target.value);
                                }} 
                                required/>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="item_description">
                            <Form.Label column sm="3">
                                Item Description:
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "15rem" }} 
                                type="text" name="item_description" 
                                placeholder="Enter item description" 
                                onChange={(e) => {
                                    setItemDescription(e.target.value);
                                }}  
                                required/>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="item_price">
                            <Form.Label column sm="3">Price: </Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "15rem" }} 
                                type="text" name="item_price" 
                                placeholder="Enter item price" 
                                onChange={(e) => {
                                    setPrice(e.target.value);
                                }}  
                                required/>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="item_location">
                            <Form.Label column sm="3">Address:</Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "15rem" }} 
                                type="text" name="item_location" 
                                placeholder="Enter item location" 
                                onChange={(e) => {
                                    setLocation(e.target.value);
                                }} 
                                required/>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="item_contact">
                            <Form.Label column sm="3">Contact:</Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "15rem" }} 
                                type="text" 
                                name="item_price" 
                                placeholder="Enter contact number" 
                                onChange={(e) => {
                                    setContact(e.target.value);
                                }}
                                required/>
                            </Col>
                        </Form.Group>
                        <Button type="sumbit" className="edit-menu-btn-primary">Add Item</Button>
                    </Form>
                    </center>
                </Col>
            </Row>
        </div> 
    )
}

export default Post;
