import React, { Component } from 'react';
import { Redirect } from 'react-router';
import axios from 'axios';
import { Button, Card, Container, Col, Row, Alert } from 'react-bootstrap';
import endPointObj from '../../endPointUrl.js';
import { Link } from "react-router-dom";
import "./Product.css";
import NavigationBar from "../Navbar/Navbar.js";


var renderedOutput;
class ProductDetails extends Component {
    constructor(props) {
        super(props);
        this.setState({
            productDetails: "",
            images: [],
            selected_image: '',

        });
        this.showImages = this.showImages.bind(this);
    }

    /*componentDidMount() {
        console.log("item id", this.props.location.state.itemId);
        if (this.props.location.state) {
            axios.get(endPointObj.url + "/product/getProduct/" + this.props.location.state.itemId)
                .then(response => {
                    console.log("Get product details response", response);
                    console.log('Product images', response.data.images)
                    var response_data = response.data;
                    if (response_data === 'ITEM_DETAILS_NOT_FOUND') {
                        this.setState({
                            noRecord: true,
                        });
                    } else {
                        this.setState({
                            productDetails: response.data,
                            images: response_data.images,
                            selected_image: response_data.images,
                            noRecord: false
                        });
                    }
                })
                .catch(err => {
                    if (err.response && err.response.data) {
                        console.log(err.response.data);
                    }
                });
        }
    }*/

    showImages = (e) => {
        renderedOutput = this.state.images.map(item =>

            <div class="images_preview" style={{ marginBottom: '15px' }}>
                <img src={endPointObj.url + '/images/products/' + item.file_name} style={{ width: '80px' }} alt="im" onClick={(e) => { this.setState({ selected_image: item.file_name }) }} />
            </div>
        )
    }

    render() {
        let itemImageSrc = null,
            itemName, itemDescription, price, location, contact,item_image,images, selected_image,
            item = this.props.location.state;
        var noRecordMessage = null;
            console.log("Itemmmm:", item);

        if (item) {
            itemImageSrc = endPointObj.url + "/images/product/" + item.item_image;
            itemName = item.itemName;
            itemDescription = item.itemDescription;
            price = item.price;
            location = item.location;
            contact = item.contact;
            images = item.images;
            selected_image= item.images;
        }
        if (this.state && this.state.noRecord) {
            noRecordMessage = (
                <Alert variant="warning">
                    Product description is not available at this time!
                </Alert>
            );
        }

        renderedOutput = images.map(item =>

            <div class="images_preview" style={{ marginBottom: '15px' }}>
                <img src={endPointObj.url + '/images/products/' + item.file_name} style={{ width: '80px' }} alt="im" onClick={(e) => { this.setState({ selected_image: item.file_name }) }} />
            </div>
        )

        return (
            <div style={{paddingLeft:"0em", paddingRight:"-30em"}}>
                <NavigationBar />

                <Card style={{ width: "30rem", height: "20rem", margin: "2%", backgroundColor: "#ffffff", paddingRight:"0em" }}>
                    
                <Col md={12}>
                    <Row >
                            <Card.Img style={{ width: "30rem", height: "20rem" }} src={itemImageSrc} />
                    </Row>
                        
                        <Card.Body>
                            <Card.Title><h2 className="productdetailsTitle"><b>{itemName}</b> &nbsp;<label style={{ fontSize:"40px", color:"#76b900" }}><u>${price}</u></label></h2></Card.Title>
                            <hr/>
                            <Card.Text className="productdetailsDescription"><b>{itemDescription}</b> </Card.Text>
                            <br/>
                            <Card.Text className="productdetailsDescription"><u>Contact Information</u>: {location} | {contact}</Card.Text>
                        </Card.Body>
                </Col>
                </Card>

                {this.showImages()}
                {renderedOutput}
                <div className="col-sm-4">
                           
                    {images[0] != null ?
                        <img src={endPointObj.url + '/images/products/' + selected_image} style={{ width: '450px' }} class="image-fluid"></img> :
                        null}

                </div>
                <br/>
            </div>
        )
    }
}
export default ProductDetails;
