import React, { useEffect, useState } from 'react';
import axios from 'axios';
import endPointObj from '../endPointUrl.js';
import { Form, Col, Row, Button, Card, Modal } from "react-bootstrap";
import { Link } from 'react-router-dom';
import NavigationBar from "./Navbar/Navbar.js";
import "../App.css"; 


function Post() {

    //item -> Current state , addItem -> Function to set current set.
    const [itemId, setItemId] = useState('');
    const [itemName, setItemName] = useState('');
    const [itemDescription, setItemDescription] = useState('');
    const [price, setPrice] = useState('');
    const [location, setLocation] = useState('');
    const [contact, setContact] = useState('');
    const [itemImage, setItemImage] = useState();
    const [fileText, setFileText] = useState('Choose file...');
    const [file, setFile] = useState('');
    const [alert, setAlert] = useState('');
    const [showModal, setModal] = useState(false);


    const addItem = (e) => {
        e.preventDefault();
        axios.post(endPointObj.url + "/marketplace/addProduct", {
            itemName: itemName,
            itemDescription:itemDescription,
            price: price,
            location: location,
            contact: contact    
        })
        .then((response) => {
            console.log("Product list after adding item", response.data);
            console.log("Product list after adding item", response.data.itemId);

            setItemId(response.data.itemId);
            setAlert('Item successfully added');
        })
        .catch((err) => {
            console.log('error');
            if (err.response && err.response.data) {
                setAlert(err);
            }
        });
    }

    const onUpload = (e) => {
        e.preventDefault();
        const formData = new FormData();
        console.log("files",file);
        console.log("item id", itemId);
        formData.append("itemimage", file);
        const uploadConfig = {
            headers: {
                "content-type": "multipart/form-data"
            }
        };
        axios.post(endPointObj.url + '/marketplace/uploads/item/' + itemId, formData, uploadConfig)
            .then(response => {    
                setFileText("Choose file...");
                setItemImage(response.data);              
            })
            .catch(err => {
                console.log("Error", err);
            });
    }

    const openModal = () => {
        setModal(true);
        
    };
    
    const onClose = (e) => {
        setModal(false);
    }


    return(
      <div><NavigationBar />  
      <div className = "postContainer">
            
            
                <Col xs={12} md={12}>
                    <center>
                    <br/>
                    <h3 className="commonTextLabel" style={{ paddingRight:"10em" }}>Add New Product Item </h3><br /> 
                    <Form onSubmit={addItem}>
                        <Form.Group as={Row} controlId="item_name" className="textLabel">
                           
                            <Form.Label style={{ width: "10rem" }}>
                                Item Name:
                            </Form.Label>
                            
                            <Col sm="1">
                                <Form.Control style={{ width: "25rem", height: "3rem" }} 
                                type="text" name="item_name" 
                                placeholder="Enter item name" 
                                onChange={(e) => {
                                    setItemName(e.target.value);
                                }} 
                                required/>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="item_description" className="textLabel">
                            <Form.Label column sm="3">
                                Item Description:
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "25rem", height: "3rem" }}  
                                type="text" name="item_description" 
                                placeholder="Enter item description" 
                                onChange={(e) => {
                                    setItemDescription(e.target.value);
                                }}  
                                required/>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="item_price" className="textLabel">
                            <Form.Label column sm="3">Price: </Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "25rem", height: "3rem" }}  
                                type="text" name="item_price" 
                                placeholder="Enter item price" 
                                onChange={(e) => {
                                    setPrice(e.target.value);
                                }}  
                                required/>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="item_location" className="textLabel">
                            <Form.Label column sm="3">Address:</Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "25rem", height: "3rem" }} 
                                type="text" name="item_location" 
                                placeholder="Enter item location" 
                                onChange={(e) => {
                                    setLocation(e.target.value);
                                }} 
                                required/>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="item_contact" className="textLabel">
                            <Form.Label column sm="3">Contact:</Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "25rem", height: "3rem" }} 
                                type="text" 
                                name="item_price" 
                                placeholder="Enter contact number" 
                                onChange={(e) => {
                                    setContact(e.target.value);
                                }}
                                required/>
                            </Col>
                        </Form.Group>
                        <div style={{ paddingRight:"10em" }}> 
                        <Button type="sumbit" className="viewImageButton">Add Item</Button>
                        </div><br/><br/>
                        <div style={{ paddingRight:"10em", color:"#76b900", fontSize:"1.5em", fontFamily:"DINWebPro-Medium"}}><b>{alert}</b></div>
                        <br/><br/>
                        <center>
                        <Button variant="primary" onClick={openModal} name="viewImages" className="viewImageButton">Upload Product Image</Button>
                        </center>
                    </Form>
                    </center>
                </Col>
                
            </div> 
                
            <div id="myModal" className="Modal is-hidden">
                    <Modal show={showModal} onHide={onClose}>
                        <Modal.Body>
                        <center>
                        <br/><br/>
                        <h3 className="commonTextLabel">Upload Item Image</h3><br />
                
                        <Card style={{ width: '12rem', height: '12rem' }}>
                            <Card.Img variant="top" style={{ width: '12rem', height: '12rem' }} src={endPointObj.url + '/images/product/' +itemImage} />
                        </Card><br/><br/>
                        <form onSubmit={onUpload}><br />
                            <div class="custom-file" style={{ width: "80%" }}>
                                <input type="file" class="custom-file-input" name="image" accept="image/*" 
                                onChange={(e) => {
                                    setFileText(e.target.files[0].name);
                                    setFile(e.target.files[0]);
                                }}  
                                required />
                                <label class="custom-file-label" for="image" style={{"text-align":"left"}}>{fileText}</label>
                            </div><br /><br />
                            <Button type="submit" className="viewImageButton">Upload</Button>
                        </form>
                        </center>
                        </Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" className="Close" onClick={onClose}>Close</Button>
                        </Modal.Footer>
                    </Modal>
                </div>
                    
                
            
       
        </div>
    )
}

export default Post;
