import React, { Component } from "react";
import { Card } from "react-bootstrap";
import { Link } from "react-router-dom";
import endPointObj from '../../endPointUrl.js';


class PostCard extends Component {
 
  render() {

    var resData = this.props.posts;
    let imageSrc = endPointObj.url + "/images/restaurant/" + this.props.posts.item_image;
    return (
      <Card bg="white" style={{ width: "20rem", margin: "5%" }}>
      <Link to={{pathname: '/restaurant', state: resData}}>
      <Card bg="white" style={{ width: "18rem", margin: "5%" }}>
        
        <Card.Body>
          <Card.Title style={{ fontSize: "2rem", margin: "5%", color: "#1d6e04" }}><b>{this.props.posts.itemName}</b></Card.Title>
          <Card.Img
          style={{ width: "15rem", height: "10em", margin: "5%" }}
          src={imageSrc}
        />
          <Card.Text>{this.props.posts.itemDescription}</Card.Text>
        </Card.Body>
      </Card>
      </Link>
      </Card>
    );
  }
}

export default PostCard;
