import React, { useEffect, useState } from 'react';
import axios from 'axios';
import endPointObj from '../endPointUrl.js';
import { Form, Col, Row, Button, Card } from "react-bootstrap";
import { Link } from 'react-router-dom';
import NavigationBar from "./Navbar/Navbar.js";
import "../App.css"; 


function Post() {

    //item -> Current state , addItem -> Function to set current set.
    const [itemName, setItemName] = useState('');
    const [itemDescription, setItemDescription] = useState('');
    const [price, setPrice] = useState('');
    const [location, setLocation] = useState('');
    const [contact, setContact] = useState('');
    const [itemId, setItemId] = useState('');
    const [itemImage, setItemImage] = useState('');
    const [fileText, setFileText] = useState('Choose file...');
    const [file, setFile] = useState('');
    const [alert, setAlert] = useState('');




    const addItem = (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append("itemimage", file);
        formData.append(itemName, itemName);
        formData.append(itemDescription, itemDescription);
        formData.append(price, price);
        formData.append(location, location);
        formData.append(contact, contact);

        const uploadConfig = {
            headers: {
                "content-type": "multipart/form-data"
            }
        };

        axios.post(endPointObj.url + "/marketplace/addProduct", formData, uploadConfig)
        .then((response) => {
            console.log("Product list after adding item", response.data);
            setItemId(response.data.itemId);
            setAlert('Item successfully added');
        })
        .catch((err) => {
            console.log('error');
            if (err.response && err.response.data) {
                setAlert(err);
            }
        });
    }

    /*const onUpload = (e) => {
        e.preventDefault();
        const formData = new FormData();
        console.log("files",file);

        formData.append("itemimage", file);
        const uploadConfig = {
            headers: {
                "content-type": "multipart/form-data"
            }
        };
        axios.post(endPointObj.url + '/marketplace/uploads/item/' + itemId, formData, uploadConfig)
            .then(response => {    
                setFileText("Choose file...");
                setItemImage(response.data);              
            })
            .catch(err => {
                console.log("Error", err);
            });
    }*/


    return(
      <div className = "postContainer">
           <NavigationBar />


            <Row>
                <Col xs={6} md={6}>
                    <center>
                    <br/><br/>
                    <h3 className="commonTextLabel">Add New Product Item</h3><br /> 
                    <Form onSubmit={addItem}>

                    <Card style={{ width: '4rem', height: '2rem' }}>
                            <Card.Img variant="top" style={{ width: '8rem', height: '8rem' }} src={endPointObj.url + '/images/product/' +itemImage} />
                        </Card><br/><br/>
                        
                            <div class="custom-file" style={{ width: "80%" }}>
                                <input type="file" class="custom-file-input" name="image" accept="image/*" 
                                onChange={(e) => {
                                    setFileText(e.target.files[0].name);
                                    setFile(e.target.files[0]);
                                }}  
                                required />
                                <label class="custom-file-label" for="image" style={{"text-align":"left"}}>{fileText}</label>
                            </div><br /><br />
                            

                        <Form.Group as={Row} controlId="item_name" className="textLabel">
                            <Form.Label column sm="3">
                                Item Name:
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "15rem" }} 
                                type="text" name="item_name" 
                                placeholder="Enter item name" 
                                onChange={(e) => {
                                    setItemName(e.target.value);
                                }} 
                                required/>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="item_description" className="textLabel">
                            <Form.Label column sm="3">
                                Item Description:
                            </Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "15rem" }} 
                                type="text" name="item_description" 
                                placeholder="Enter item description" 
                                onChange={(e) => {
                                    setItemDescription(e.target.value);
                                }}  
                                required/>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="item_price" className="textLabel">
                            <Form.Label column sm="3">Price: </Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "15rem" }} 
                                type="text" name="item_price" 
                                placeholder="Enter item price" 
                                onChange={(e) => {
                                    setPrice(e.target.value);
                                }}  
                                required/>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="item_location" className="textLabel">
                            <Form.Label column sm="3">Address:</Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "15rem" }} 
                                type="text" name="item_location" 
                                placeholder="Enter item location" 
                                onChange={(e) => {
                                    setLocation(e.target.value);
                                }} 
                                required/>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="item_contact" className="textLabel">
                            <Form.Label column sm="3">Contact:</Form.Label>
                            <Col sm="4">
                                <Form.Control style={{ width: "15rem" }} 
                                type="text" 
                                name="item_price" 
                                placeholder="Enter contact number" 
                                onChange={(e) => {
                                    setContact(e.target.value);
                                }}
                                required/>
                            </Col>
                        </Form.Group>
                        <center><Button type="sumbit" className="viewImageButton">Add Item</Button></center>
                    </Form>
                    </center>
                </Col>
                <Col xs={6} md={4}>
                    <center>
                        <br/><br/>
                        <h3 className="commonTextLabel">Upload Item Image</h3><br />
                
                        
                    </center>
                </Col>
            </Row>
        </div> 
    )
}

export default Post;
